import { Component } from '@angular/core';
import { Iproduct } from '../iproduct';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  ProductList: Iproduct[] =[];
  constructor(){
    this.ProductList =[
      {id: 1, name: 'VASE', description: 'anything', price: 500, onsale: false, discount: 5, quantity: 7, image: 'assets/images/22222.png'},
      {id: 2, name: 'CLOCK', description: 'anything', price: 500, onsale: false, discount: 5, quantity: 7, image: 'assets/images/h4.png'},
      {id: 3, name: 'DISH', description: 'anything', price: 500, onsale: false, discount: 5, quantity: 7, image: 'assets/images/dish.png'},
      {id: 4, name: 'camera', description: 'anything', price: 500, onsale: false, discount: 5, quantity: 7, image: 'assets/images/camera.jpg'},
      {id: 5, name: 'camera', description: 'anything', price: 500, onsale: false, discount: 5, quantity: 7, image: 'assets/images/camera.jpg'},
    ]
  }
}
