import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../services/product.service';
import { Router, ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-product-form',
  templateUrl: './product-form.component.html',
  styleUrls: ['./product-form.component.css']
})
export class ProductFormComponent {
  productForm: FormGroup = new FormGroup({
    name: new FormControl('', [
      Validators.required,
      Validators.minLength(5)
    ]),
    image: new FormControl('assets/images/2222.png', [

    ]),
    description: new FormControl('', [
      Validators.required,
      Validators.minLength(4)
    ]),
    price: new FormControl(0, [
      Validators.required,
      Validators.min(100)
    ]),
    quantity: new FormControl(0, [
      Validators.required,
      Validators.min(5)
    ]),
    discount: new FormControl(0, [
      Validators.required,
      Validators.min(5),
      Validators.max(25)
    ]),
    onsale: new FormControl(false, [
      Validators.required,
    ]),
  })
  get nameControl() {
    return this.productForm.get('name');
  }

  get descriptionControl() {
    return this.productForm.get('description');
  }

  get priceControl() {
    return this.productForm.get('price');
  }

  get quantityControl() {
    return this.productForm.get('quantity');
  }

  get discountControl() {
    return this.productForm.get('discount');
  }


  constructor(private prodService: ProductService, private myRouter: Router, private actRoute: ActivatedRoute) { }
  id: number = 0;
  OnInit(): void {
    this.id = this.actRoute.snapshot.params['id'];
    if (this.id != 0) {

      }
      // var product = this.prodService.getProductById(this.id);
      // this.productForm.controls['name'].setValue(product?.name);
      // this.productForm.controls['price'].setValue(product?.price);
      // this.productForm.controls['quantity'].setValue(product?.quantity);
      // this.productForm.controls['description'].setValue(product?.description);
      // this.productForm.controls['onsale'].setValue(product?.onsale);
      // this.productForm.controls['discount'].setValue(product?.discount);
      // this.productForm.controls['image'].setValue(product?.image);

    }
  }
  onSubmit(e: Event) {
    e.preventDefault;
    if (this.productForm.valid) {
      if(this.id){

      }
      else{
      this.prodService.addProduct(this.productForm.value);
      }
      this.myRouter.navigate(['./products']);
    }


  }


}
<div class="content row">
    <div class="col-6">
      <img src="assets/images/1.png" alt="" />
      <h3>Add Product</h3>
    </div>
    <form [formGroup]="productForm" class="col-5 p-5" (submit)="onSubmit($event)">
      <h4 class="text-center">PRODUCTS FORM</h4>
      <br>
      <div class="mb-3">
        <label for="name" class="form-label">NAME</label>
        <input formControlName="name" [ngClass]="{'is-valid': nameControl?.valid , 'is-invalid': nameControl?.errors && nameControl?.touched }"
          name="name" type="text" class="form-control" id="name" required>
        <div class="valid-feedback">Looks good!</div>
        <div class="invalid-feedback">
          <ng-container *ngIf=" nameControl?.hasError('required')">
            Name is required
          </ng-container>
          <ng-container *ngIf=" nameControl?.hasError('minlength')">
            Name must be at least 5 characters length
          </ng-container>
        </div>
      </div>
      <div class="mb-3">
        <label for="price" class="form-label">Price $</label>
        <input formControlName="price" name="price" 
        [ngClass]="{'is-valid': priceControl?.valid , 'is-invalid': priceControl?.errors && priceControl?.touched }"
        type="number" class="form-control" id="price" required>
        <div class="valid-feedback">Looks good!</div>
        <div class="invalid-feedback"><ng-container *ngIf=" priceControl?.hasError('required')">
            Price is required
          </ng-container>
          <ng-container *ngIf=" priceControl?.hasError('min')">
            Price must be at least 100$
          </ng-container>
        </div>
      </div>
      <div class="mb-3">
        <label for="quantity" class="form-label">Quantity</label>
        <input formControlName="quantity" name="quantity"
        [ngClass]="{'is-valid': quantityControl?.valid , 'is-invalid': quantityControl?.errors && quantityControl?.touched }"
        type="number" class="form-control" id="quantity" required>
        <div class="valid-feedback">Looks good!</div>
        <div class="invalid-feedback"><ng-container *ngIf=" quantityControl?.hasError('required')">
            Quantity is required
          </ng-container>
          <ng-container *ngIf=" quantityControl?.hasError('min')">
            Quantity must be at least 5
          </ng-container>
        </div>
      </div>
      <div class="mb-3">
        <label for="discount" class="form-label">Discount %</label>
        <input formControlName="discount" name="discount"
        [ngClass]="{'is-valid': discountControl?.valid , 'is-invalid': discountControl?.errors && discountControl?.touched }"
         type="number" class="form-control" id="discount" required>
        <div class="valid-feedback">Looks good!</div>
        <div class="invalid-feedback"><ng-container *ngIf=" discountControl?.hasError('required')">
            Discount is required
          </ng-container>
          <ng-container *ngIf="discountControl?.errors?.['min']">
            Discount must be Between 5% and 25%
          </ng-container>
        </div>
      </div>
      <div class="mb-3">
        <label for="description" class="form-label">Description</label>
        <input formControlName="description" name="description"
          [ngClass]="{'is-valid': descriptionControl?.valid , 'is-invalid': descriptionControl?.errors && descriptionControl?.touched }"
          type="string" class="form-control" id="description" required>
        <div class="valid-feedback">Looks good!</div>
        <div class="invalid-feedback"><ng-container *ngIf=" descriptionControl?.hasError('required')">
            Description is required
          </ng-container>
          <ng-container *ngIf=" descriptionControl?.hasError('minlength')">
            Description must be at least 4 characters length
          </ng-container>
        </div>
      </div>
      <div class="mb-3 form-check">
        <input formControlName="onsale" name="onsale" type="checkbox" class="form-check-input" id="check">
        <label class="form-check-label" for="check">On Sale</label>
      </div>
      <br>
      <div class="submitbtn">
        <button [disabled]="!productForm.valid" type="submit" class="btn">Submit</button>
      </div>
    </form>
  </div>
